function App() {
  const ctx=useContext(AuthContext)


  return (


      <React.Fragment>
      <MainHeader   />
      <main>
        {!ctx.isLoggedIn && <Login  />}
        {ctx.isLoggedIn && <Home onLogout={logoutHandler} />}
